(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'stripe_securityupdate_q3_2024', 'update', 'en_US', 'Security update required for your Stripe extension', 'To continue securely accepting payments, you must update your Stripe extension to at least version 8.6, re-authenticate, and turn on the new checkout experience. Review the documentation for instructions on how to make the necessary changes from your WooCommerce dashboard.', '{}', 'pending', 'woocommerce.com', '2024-10-20 22:36:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protege tu tienda de WooCommerce con Jetpack Backup.', 'Tener una tienda inactiva implica una pérdida de ventas. Las restauraciones en un solo clic te ayudan a volver a estar operativo rápidamente si hay algún problema.', '{}', 'unactioned', 'woocommerce-admin-notes', '2024-11-07 16:48:31', NULL, 0, 'thumbnail', 'http://localhost/gala/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info');

